"use strict";

function generatePass(len, strong) {
    let password = "";
    let symbols = "abcdefghijklmnopqrstuvwxyz0123456789!№%?*_";

    // Выбор сложности пароля
    if (strong === false) symbols = "abcdefghijklmnopqrstuvwxyz";

    for (let i = 0; i < len; i++) {
        let random = getRandomInt(2); // Рандомно переводим символ в upperCase
        let charPass = symbols.charAt(Math.floor(Math.random() * symbols.length));
        if (random === 1) charPass = charPass.toUpperCase();
        password += charPass;
    }
    return password;
}

// метод для установки границы рандомного числа
function getRandomInt(max) {
    return Math.floor(Math.random() * max);
}

module.exports = { generatePass };