"use strict";

function sum(a, b) {
    return a + b;
};

function subtract(a, b) {
    return a - b;
};

function diff(a, b) {
    return a / b;
};

function mult(a, b) {
    return a * b;
};

module.exports = { sum, subtract, diff, mult };
